mapper.select:
SELECT COUNT(*) 
FROM Product 
WHERE seriesNum = #{seriesNum}

SELECT COUNT(*) 
FROM Color 
WHERE productNum = #{productNum}

SELECT COUNT(*) 
FROM Display 
WHERE productNum = #{productNum}

SELECT COUNT(*) 
FROM Storage 
WHERE productNum = #{productNum}

SELECT COUNT(*) 
FROM ProductDetail 
WHERE colorNum = #{colorNum}

SELECT COUNT(*) 
FROM ProductDetail 
WHERE displayNum = #{displayNum}

SELECT COUNT(*) 
FROM ProductDetail 
WHERE storageNum = #{storageNum}

SELECT COUNT(*) 
FROM History 
WHERE productDetailNum = #{productDetailNum}

mapper.update:

UPDATE Store
SET storeStatus = '폐업'
WHERE storeNum = #{storeNum}


UPDATE User
SET userStatus = '탈퇴'
WHERE userNum = #{userNum}


UPDATE Receipt
SET shippingStatus = '배송 중'
WHERE receiptNum = #{receiptNum}


UPDATE Receipt
SET shippingStatus = '배송 완료'
WHERE receiptNum = #{receiptNum}
